import React, { useState, useEffect } from 'react';

// Define the backend API URL
const API_URL = 'http://localhost:5000/api';

// --- Reusable Components ---
const StatusPill = ({ status }) => {
    const statusClasses = {
        Pending: 'bg-yellow-100 text-yellow-800',
        'In Transit': 'bg-blue-100 text-blue-800',
        Delivered: 'bg-green-100 text-green-800',
        Cancelled: 'bg-red-100 text-red-800',
    };
    return <span className={`px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${statusClasses[status] || 'bg-gray-100 text-gray-800'}`}>{status}</span>;
};

const Modal = ({ title, children, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
        <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md">
            <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-bold">{title}</h3>
                <button onClick={onClose} className="text-gray-500 hover:text-gray-800 text-2xl">&times;</button>
            </div>
            {children}
        </div>
    </div>
);

const TabButton = ({ label, isActive, onClick }) => (
    <button onClick={onClick} className={`px-4 py-2 text-sm font-medium rounded-md ${isActive ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}>
        {label}
    </button>
);

// --- Auth Pages ---
function AuthPage({ onLogin, onRegister }) {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            if (isLogin) {
                await onLogin(email, password);
            } else {
                if (password.length < 6) {
                    setError("Password must be at least 6 characters.");
                    return;
                }
                await onRegister(name, email, password);
                setIsLogin(true); // Switch to login view after successful registration
                alert('Registration successful! Please log in.');
            }
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
            <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">{isLogin ? 'Login' : 'Admin Registration'}</h2>
                <form onSubmit={handleSubmit}>
                    {!isLogin && <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Full Name" className="p-2 border rounded w-full mb-4" required />}
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="p-2 border rounded w-full mb-4" required />
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="p-2 border rounded w-full mb-4" required />
                    {error && <p className="text-red-500 text-xs mb-4">{error}</p>}
                    <button type="submit" className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded w-full">{isLogin ? 'Sign In' : 'Register'}</button>
                </form>
            </div>
            <div className="text-center mt-4">
                <button onClick={() => { setIsLogin(!isLogin); setError(''); }} className="text-blue-600 hover:underline">
                    {isLogin ? "Need an account? Register" : "Already have an account? Login"}
                </button>
            </div>
        </div>
    );
}

// --- Admin Dashboard ---
function AdminDashboard({ onLogout, customers, agents, deliveries, onAddCustomer, onAddAgent, onCreateDelivery, onUpdateCustomer, onDeleteCustomer, onUpdateAgent, onDeleteAgent, onUpdateDelivery, onDeleteDelivery }) {
    const [activeTab, setActiveTab] = useState('deliveries');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalType, setModalType] = useState('');
    const [formState, setFormState] = useState({});
    const planTypes = ['Daily', 'Weekly', 'Monthly', 'Bi-Weekly'];
    const fruitBowlTypes = ['Tropical Fruit Bowl', 'Berry Blast Bowl', 'Citrus Mix', 'Custom'];
  
    const openModal = (type, item = null) => {
        setModalType(type);
        if (item) { setFormState(item); } 
        else {
            const defaultState = {
                addCustomer: { name: '', address: '', mobile: '', plan: planTypes[0] },
                addAgent: { name: '', mobile: '', email: '', password: '' },
                createDelivery: { customerId: '', agentId: '', item: fruitBowlTypes[0], delivery_date: new Date().toISOString().split('T')[0] }
            };
            setFormState(defaultState[type]);
        }
        setIsModalOpen(true);
    };
  
    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormState(prev => ({ ...prev, [name]: value }));
    };
  
    const handleSubmit = (e) => {
        e.preventDefault();
        switch (modalType) {
            case 'addCustomer': onAddCustomer(formState); break;
            case 'addAgent': onAddAgent(formState); break;
            case 'createDelivery': onCreateDelivery(formState); break;
            case 'editCustomer': onUpdateCustomer(formState); break;
            case 'editAgent': onUpdateAgent(formState); break;
            case 'editDelivery': onUpdateDelivery(formState); break;
            default: break;
        }
        setIsModalOpen(false);
    };
  
    const renderModalContent = () => {
        if (!isModalOpen) return null;
        switch (modalType) {
            case 'addCustomer': case 'editCustomer':
                return (
                    <form onSubmit={handleSubmit}>
                        <input name="name" value={formState.name || ''} onChange={handleFormChange} placeholder="Name" className="p-2 border rounded w-full mb-2" required/>
                        <input name="address" value={formState.address || ''} onChange={handleFormChange} placeholder="Address" className="p-2 border rounded w-full mb-2" required/>
                        <input name="mobile" value={formState.mobile || ''} onChange={handleFormChange} placeholder="Mobile" className="p-2 border rounded w-full mb-2" required/>
                        <button type="submit" className="bg-blue-600 text-white w-full py-2 rounded">Save Customer</button>
                    </form>
                );
            case 'addAgent': case 'editAgent':
                return (
                    <form onSubmit={handleSubmit}>
                        <input name="name" value={formState.name || ''} onChange={handleFormChange} placeholder="Agent Name" className="p-2 border rounded w-full mb-2" required/>
                        <input name="mobile" value={formState.mobile || ''} onChange={handleFormChange} placeholder="Agent Mobile" className="p-2 border rounded w-full mb-2" required/>
                        <input type="email" name="email" value={formState.email || ''} onChange={handleFormChange} placeholder="Login Email" className="p-2 border rounded w-full mb-2" required/>
                        <input type="password" name="password" value={formState.password || ''} onChange={handleFormChange} placeholder="Login Password" className="p-2 border rounded w-full mb-4" required={modalType === 'addAgent'}/>
                        <button type="submit" className="bg-blue-600 text-white w-full py-2 rounded">Save Agent</button>
                    </form>
                );
            case 'createDelivery': case 'editDelivery':
                return (
                    <form onSubmit={handleSubmit}>
                        <select name="customerId" value={formState.customerId || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2" required><option value="">Select Customer</option>{customers.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}</select>
                        <select name="agentId" value={formState.agentId || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2" required><option value="">Assign to Agent</option>{agents.map(a => <option key={a.id} value={a.id}>{a.name}</option>)}</select>
                        <input type="date" name="delivery_date" value={formState.delivery_date?.split('T')[0] || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2" required />
                        <select name="item" value={formState.item || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2">{fruitBowlTypes.map(b => <option key={b} value={b}>{b}</option>)}</select>
                        <select name="status" value={formState.status || 'Pending'} onChange={handleFormChange} className="p-2 border rounded w-full mb-4"><option>Pending</option><option>In Transit</option><option>Delivered</option><option>Cancelled</option></select>
                        <button type="submit" className="bg-green-500 text-white w-full py-2 rounded">Save Delivery</button>
                    </form>
                );
            default: return null;
        }
    };
  
    return (
      <div className="p-6 bg-gray-50 min-h-screen">
        {isModalOpen && <Modal title={modalType.includes('edit') ? 'Edit Details' : 'Add New'} onClose={() => setIsModalOpen(false)}>{renderModalContent()}</Modal>}
        <div className="flex justify-between items-center mb-6"><h1 className="text-3xl font-bold text-gray-800">Admin Dashboard</h1><button onClick={onLogout} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-lg">Logout</button></div>
        <div className="flex gap-2 mb-6"><TabButton label="Deliveries" isActive={activeTab === 'deliveries'} onClick={() => setActiveTab('deliveries')} /><TabButton label="Customers" isActive={activeTab === 'customers'} onClick={() => setActiveTab('customers')} /><TabButton label="Agents" isActive={activeTab === 'agents'} onClick={() => setActiveTab('agents')} /></div>
        <div className="bg-white shadow-md rounded-lg p-6 overflow-x-auto">
            {activeTab === 'deliveries' && (
                <><button onClick={() => openModal('createDelivery')} className="bg-green-500 text-white py-2 px-4 rounded mb-4">+ Create Delivery</button>
                <table className="min-w-full"><thead><tr><th className="text-left p-2">Customer</th><th className="text-left p-2">Agent</th><th className="text-left p-2">Status</th><th className="text-left p-2">Actions</th></tr></thead>
                <tbody>{deliveries.map(d => {const c = customers.find(c => c.id === d.customerId); const a = agents.find(a => a.id === d.agentId); return (<tr key={d.id} className="border-b"><td className="p-2">{c?.name || 'N/A'}</td><td className="p-2">{a?.name || 'Unassigned'}</td><td className="p-2"><StatusPill status={d.status} /></td><td className="p-2 whitespace-nowrap"><button onClick={() => openModal('editDelivery', d)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteDelivery(d.id)} className="text-red-600">Delete</button></td></tr>);})}</tbody></table></>
            )}
            {activeTab === 'customers' && (
                <><button onClick={() => openModal('addCustomer')} className="bg-blue-600 text-white py-2 px-4 rounded mb-4">+ Add Customer</button>
                <table className="min-w-full"><thead><tr><th className="text-left p-2">Name</th><th className="text-left p-2">Address</th><th className="text-left p-2">Mobile</th><th className="text-left p-2">Actions</th></tr></thead>
                <tbody>{customers.map(c => (<tr key={c.id} className="border-b"><td className="p-2">{c.name}</td><td className="p-2">{c.address}</td><td className="p-2">{c.mobile}</td><td className="p-2 whitespace-nowrap"><button onClick={() => openModal('editCustomer', c)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteCustomer(c.id)} className="text-red-600">Delete</button></td></tr>))}</tbody></table></>
            )}
            {activeTab === 'agents' && (
                <><button onClick={() => openModal('addAgent')} className="bg-blue-600 text-white py-2 px-4 rounded mb-4">+ Add Agent</button>
                <table className="min-w-full"><thead><tr><th className="text-left p-2">Name</th><th className="text-left p-2">Email</th><th className="text-left p-2">Actions</th></tr></thead>
                <tbody>{agents.map(a => (<tr key={a.id} className="border-b"><td className="p-2">{a.name}</td><td className="p-2">{a.email}</td><td className="p-2 whitespace-nowrap"><button onClick={() => openModal('editAgent', a)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteAgent(a.id)} className="text-red-600">Delete</button></td></tr>))}</tbody></table></>
            )}
        </div>
      </div>
    );
  }

// --- Agent Portal ---
function AgentPortal({ agent, onLogout, onUpdateDelivery }) {
    // This component would also fetch its data from the backend
    return <div><h1 className="text-2xl">Welcome, {agent.name} (Agent Portal)</h1><button onClick={onLogout}>Logout</button></div>;
}

// --- Main App Component ---
export default function App() {
  const [page, setPage] = useState('auth');
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  
  const [customers, setCustomers] = useState([]);
  const [agents, setAgents] = useState([]);
  const [deliveries, setDeliveries] = useState([]);

  const authHeader = { 'Authorization': `Bearer ${token}` };
  
  const fetchData = async () => {
    if (!token) return;
    try {
        const [customersRes, agentsRes, deliveriesRes] = await Promise.all([
            fetch(`${API_URL}/customers`, { headers: authHeader }),
            fetch(`${API_URL}/agents`, { headers: authHeader }),
            fetch(`${API_URL}/deliveries`, { headers: authHeader })
        ]);
        setCustomers(await customersRes.json());
        setAgents(await agentsRes.json());
        setDeliveries(await deliveriesRes.json());
    } catch (error) {
        console.error("Failed to fetch data:", error);
        handleLogout();
    }
  };

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (token && user) {
        const parsedUser = JSON.parse(user);
        setLoggedInUser(parsedUser);
        setPage(parsedUser.role === 'Admin' ? 'admin_dashboard' : 'agent_portal');
    }
  }, [token]);

  useEffect(() => {
      if(loggedInUser) {
          fetchData();
      }
  }, [loggedInUser]);

  // THIS IS THE FIX: Robust error handling for API calls
  const handleApiError = async (response) => {
      if (!response.ok) {
          try {
              // Try to parse a JSON error message from the backend
              const errorData = await response.json();
              throw new Error(errorData.message || 'An unknown error occurred');
          } catch (e) {
              // If parsing fails, it's likely an HTML error page
              throw new Error(`Server error: ${response.status} ${response.statusText}`);
          }
      }
      return response;
  };

  const handleRegister = async (name, email, password) => {
    const res = await fetch(`${API_URL}/auth/register`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, password, role: 'Admin' })
    });
    await handleApiError(res);
  };

  const handleLogin = async (email, password) => {
    const res = await fetch(`${API_URL}/auth/login`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
    });
    await handleApiError(res);
    const { token, user } = await res.json();
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
    setToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setToken(null);
    setLoggedInUser(null);
    setPage('auth');
  };

  // --- API-Driven Data Handlers ---
  const apiRequest = async (endpoint, method, body = null) => {
      try {
        const options = {
            method,
            headers: { ...authHeader, 'Content-Type': 'application/json' }
        };
        if (body) options.body = JSON.stringify(body);
        const res = await fetch(`${API_URL}${endpoint}`, options);
        await handleApiError(res);
        fetchData(); // Refetch all data to update UI
      } catch (error) {
          console.error(`API request to ${endpoint} failed:`, error);
          alert(`Error: ${error.message}`);
      }
  };

  const handleAddCustomer = (customer) => apiRequest('/customers', 'POST', customer);
  const handleUpdateCustomer = (customer) => apiRequest(`/customers/${customer.id}`, 'PUT', customer);
  const handleDeleteCustomer = (id) => { if (window.confirm('Are you sure?')) apiRequest(`/customers/${id}`, 'DELETE'); };

  const handleAddAgent = (agent) => apiRequest('/agents', 'POST', agent);
  const handleUpdateAgent = (agent) => apiRequest(`/agents/${agent.id}`, 'PUT', agent);
  const handleDeleteAgent = (id) => { if (window.confirm('Are you sure?')) apiRequest(`/agents/${id}`, 'DELETE'); };

  const handleCreateDelivery = (delivery) => apiRequest('/deliveries', 'POST', delivery);
  const handleUpdateDelivery = (delivery) => apiRequest(`/deliveries/${delivery.id}`, 'PUT', delivery);
  const handleDeleteDelivery = (id) => { if (window.confirm('Are you sure?')) apiRequest(`/deliveries/${id}`, 'DELETE'); };


  switch (page) {
    case 'admin_dashboard':
      return <AdminDashboard onLogout={handleLogout} customers={customers} agents={agents} deliveries={deliveries} onAddCustomer={handleAddCustomer} onAddAgent={handleAddAgent} onCreateDelivery={handleCreateDelivery} onUpdateCustomer={handleUpdateCustomer} onDeleteCustomer={handleDeleteCustomer} onUpdateAgent={handleUpdateAgent} onDeleteAgent={handleDeleteAgent} onUpdateDelivery={handleUpdateDelivery} onDeleteDelivery={handleDeleteDelivery} />;
    case 'agent_portal':
      return <AgentPortal agent={loggedInUser} onLogout={handleLogout} onUpdateDelivery={handleUpdateDelivery} />;
    case 'auth':
    default:
      return <AuthPage onLogin={handleLogin} onRegister={handleRegister} />;
  }
}
