import React, { useState } from 'react'; // Removed unused 'useEffect'

// --- Configuration & Mock Data ---
const initialAgents = [
    { id: 101, name: 'Agent Vinod', mobile: '555-0101', email: 'agent@test.com', password: 'password123' },
    { id: 102, name: 'Agent Smith', mobile: '555-0102', email: 'smith@test.com', password: 'password123' },
];
const initialCustomers = [
    { id: 1, name: 'John Doe', address: '123 Main St, Anytown, AP', mobile: '123-456-7890', plan: 'Weekly' },
    { id: 2, name: 'Jane Smith', address: '456 Oak Ave, Anytown, AP', mobile: '987-654-3210', plan: 'Monthly' },
];
const initialDeliveries = [
    { id: 501, customerId: 1, agentId: 101, status: 'Pending', item: 'Tropical Fruit Bowl' },
    { id: 502, customerId: 2, agentId: 101, status: 'In Transit', item: 'Berry Blast Bowl' },
    { id: 503, customerId: 1, agentId: 102, status: 'Delivered', item: 'Tropical Fruit Bowl' },
];
const planTypes = ['Daily', 'Weekly', 'Monthly', 'Bi-Weekly'];
const fruitBowlTypes = ['Tropical Fruit Bowl', 'Berry Blast Bowl', 'Citrus Mix', 'Custom'];

// --- Reusable Components ---
const StatusPill = ({ status }) => {
    const statusClasses = {
        Pending: 'bg-yellow-100 text-yellow-800',
        'In Transit': 'bg-blue-100 text-blue-800',
        Delivered: 'bg-green-100 text-green-800',
        Cancelled: 'bg-red-100 text-red-800',
    };
    return <span className={`px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${statusClasses[status] || 'bg-gray-100 text-gray-800'}`}>{status}</span>;
};

const Modal = ({ title, children, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
        <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md">
            <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-bold">{title}</h3>
                <button onClick={onClose} className="text-gray-500 hover:text-gray-800 text-2xl">&times;</button>
            </div>
            {children}
        </div>
    </div>
);

const TabButton = ({ label, isActive, onClick }) => (
    <button onClick={onClick} className={`px-4 py-2 text-sm font-medium rounded-md ${isActive ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}>
        {label}
    </button>
);


// --- Login Page ---
function LoginPage({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('agent');

  const handleLogin = (e) => {
    e.preventDefault();
    onLogin(email, password, userType);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">FreshOnTime {userType === 'admin' ? 'Admin' : 'Agent'} Login</h2>
        <form onSubmit={handleLogin}>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="p-2 border rounded w-full mb-4" required />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="p-2 border rounded w-full mb-4" required />
          <button type="submit" className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded w-full">Sign In</button>
        </form>
      </div>
      <div className="text-center mt-4">
        <button onClick={() => setUserType(userType === 'admin' ? 'agent' : 'admin')} className="text-blue-600 hover:underline">
            Switch to {userType === 'admin' ? 'Agent' : 'Admin'} Login
        </button>
      </div>
    </div>
  );
}

// --- ADMIN PORTAL COMPONENTS ---
function AdminDashboard({ onLogout, customers, agents, deliveries, onAddCustomer, onAddAgent, onCreateDelivery, onUpdateCustomer, onDeleteCustomer, onUpdateAgent, onDeleteAgent, onUpdateDelivery, onDeleteDelivery }) {
  const [activeTab, setActiveTab] = useState('deliveries');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState('');
  
  const [formState, setFormState] = useState({});

  const openModal = (type, item = null) => {
      setModalType(type);
      if (item) {
          setFormState(item);
      } else {
          const defaultState = {
              addCustomer: { name: '', address: '', mobile: '', plan: planTypes[0] },
              addAgent: { name: '', mobile: '', email: '', password: '' },
              createDelivery: { customerId: '', agentId: '', item: fruitBowlTypes[0] }
          };
          setFormState(defaultState[type]);
      }
      setIsModalOpen(true);
  };

  const handleFormChange = (e) => {
      const { name, value } = e.target;
      if (name === 'agentId' || name === 'customerId') {
        setFormState(prev => ({ ...prev, [name]: parseInt(value, 10) }));
      } else {
        setFormState(prev => ({ ...prev, [name]: value }));
      }
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      switch (modalType) {
          case 'addCustomer': onAddCustomer(formState); break;
          case 'addAgent': onAddAgent(formState); break;
          case 'createDelivery': 
              if (!formState.customerId || !formState.agentId) return alert("Please select a customer and an agent.");
              onCreateDelivery(formState); 
              break;
          case 'editCustomer': onUpdateCustomer(formState); break;
          case 'editAgent': onUpdateAgent(formState); break;
          case 'editDelivery': onUpdateDelivery(formState); break;
          default: break;
      }
      setIsModalOpen(false);
  };

  const renderModalContent = () => {
      if (!isModalOpen) return null;
      switch (modalType) {
          case 'addCustomer':
          case 'editCustomer':
              return (
                  <form onSubmit={handleSubmit}>
                      <input name="name" value={formState.name || ''} onChange={handleFormChange} placeholder="Name" className="p-2 border rounded w-full mb-2" required/>
                      <input name="address" value={formState.address || ''} onChange={handleFormChange} placeholder="Address" className="p-2 border rounded w-full mb-2" required/>
                      <input name="mobile" value={formState.mobile || ''} onChange={handleFormChange} placeholder="Mobile" className="p-2 border rounded w-full mb-2" required/>
                      <select name="plan" value={formState.plan || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-4">{planTypes.map(p => <option key={p} value={p}>{p}</option>)}</select>
                      <button type="submit" className="bg-blue-600 text-white w-full py-2 rounded">Save Customer</button>
                  </form>
              );
          case 'addAgent':
          case 'editAgent':
              return (
                  <form onSubmit={handleSubmit}>
                      <input name="name" value={formState.name || ''} onChange={handleFormChange} placeholder="Agent Name" className="p-2 border rounded w-full mb-2" required/>
                      <input name="mobile" value={formState.mobile || ''} onChange={handleFormChange} placeholder="Agent Mobile" className="p-2 border rounded w-full mb-2" required/>
                      <input type="email" name="email" value={formState.email || ''} onChange={handleFormChange} placeholder="Login Email" className="p-2 border rounded w-full mb-2" required/>
                      <input type="password" name="password" value={formState.password || ''} onChange={handleFormChange} placeholder="Login Password" className="p-2 border rounded w-full mb-4" required={modalType === 'addAgent'}/>
                      <button type="submit" className="bg-blue-600 text-white w-full py-2 rounded">Save Agent</button>
                  </form>
              );
          case 'createDelivery':
          case 'editDelivery':
              return (
                  <form onSubmit={handleSubmit}>
                      <select name="customerId" value={formState.customerId || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2" required><option value="">Select Customer</option>{customers.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}</select>
                      <select name="agentId" value={formState.agentId || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2" required><option value="">Assign to Agent</option>{agents.map(a => <option key={a.id} value={a.id}>{a.name}</option>)}</select>
                      <select name="item" value={formState.item || ''} onChange={handleFormChange} className="p-2 border rounded w-full mb-2">{fruitBowlTypes.map(b => <option key={b} value={b}>{b}</option>)}</select>
                      <select name="status" value={formState.status || 'Pending'} onChange={handleFormChange} className="p-2 border rounded w-full mb-4"><option>Pending</option><option>In Transit</option><option>Delivered</option><option>Cancelled</option></select>
                      <button type="submit" className="bg-green-500 text-white w-full py-2 rounded">Save Delivery</button>
                  </form>
              );
          default: return null;
      }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {isModalOpen && <Modal title={modalType.includes('edit') ? 'Edit Details' : 'Add New'} onClose={() => setIsModalOpen(false)}>{renderModalContent()}</Modal>}
      <div className="flex justify-between items-center mb-6"><h1 className="text-3xl font-bold text-gray-800">Admin Dashboard</h1><button onClick={onLogout} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-lg">Logout</button></div>
      <div className="flex gap-2 mb-6"><TabButton label="Deliveries" isActive={activeTab === 'deliveries'} onClick={() => setActiveTab('deliveries')} /><TabButton label="Customers" isActive={activeTab === 'customers'} onClick={() => setActiveTab('customers')} /><TabButton label="Agents" isActive={activeTab === 'agents'} onClick={() => setActiveTab('agents')} /></div>
      <div className="bg-white shadow-md rounded-lg p-6">
          {activeTab === 'deliveries' && (
              <div>
                  <button onClick={() => openModal('createDelivery')} className="bg-green-500 text-white py-2 px-4 rounded mb-4">+ Create Delivery</button>
                  <table className="min-w-full"><thead><tr><th className="text-left p-2">Customer</th><th className="text-left p-2">Agent</th><th className="text-left p-2">Status</th><th className="text-left p-2">Actions</th></tr></thead>
                      <tbody>{deliveries.map(d => {const c = customers.find(c => c.id === d.customerId); const a = agents.find(a => a.id === d.agentId); return (<tr key={d.id} className="border-b"><td className="p-2">{c?.name || 'N/A'}</td><td className="p-2">{a?.name || 'Unassigned'}</td><td className="p-2"><StatusPill status={d.status} /></td><td className="p-2"><button onClick={() => openModal('editDelivery', d)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteDelivery(d.id)} className="text-red-600">Delete</button></td></tr>);})}</tbody>
                  </table>
              </div>
          )}
          {activeTab === 'customers' && (
              <div>
                  <button onClick={() => openModal('addCustomer')} className="bg-blue-600 text-white py-2 px-4 rounded mb-4">+ Add Customer</button>
                  <table className="min-w-full"><thead><tr><th className="text-left p-2">Name</th><th className="text-left p-2">Mobile</th><th className="text-left p-2">Actions</th></tr></thead>
                      <tbody>{customers.map(c => (<tr key={c.id} className="border-b"><td className="p-2">{c.name}</td><td className="p-2">{c.mobile}</td><td className="p-2"><button onClick={() => openModal('editCustomer', c)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteCustomer(c.id)} className="text-red-600">Delete</button></td></tr>))}</tbody>
                  </table>
              </div>
          )}
          {activeTab === 'agents' && (
              <div>
                  <button onClick={() => openModal('addAgent')} className="bg-blue-600 text-white py-2 px-4 rounded mb-4">+ Add Agent</button>
                  <table className="min-w-full"><thead><tr><th className="text-left p-2">Name</th><th className="text-left p-2">Email</th><th className="text-left p-2">Actions</th></tr></thead>
                      <tbody>{agents.map(a => (<tr key={a.id} className="border-b"><td className="p-2">{a.name}</td><td className="p-2">{a.email}</td><td className="p-2"><button onClick={() => openModal('editAgent', a)} className="text-indigo-600 mr-2">Edit</button><button onClick={() => onDeleteAgent(a.id)} className="text-red-600">Delete</button></td></tr>))}</tbody>
                  </table>
              </div>
          )}
      </div>
    </div>
  );
}

// --- AGENT PORTAL COMPONENTS ---
function AgentPortal({ agent, onLogout, allDeliveries, allCustomers, onUpdateDelivery }) {
    const [activeView, setActiveView] = useState('dashboard');
    const [selectedDelivery, setSelectedDelivery] = useState(null);
    const agentDeliveries = allDeliveries.filter(d => d.agentId === agent.id);

    const handleSaveStatus = (updatedDelivery) => {
        onUpdateDelivery(updatedDelivery);
    };
    
    const renderView = () => {
        const props = { deliveries: agentDeliveries, customers: allCustomers };
        switch(activeView) {
            case 'history': return <DeliveryHistoryView {...props} />;
            case 'profile': return <AgentProfileView agent={agent} onLogout={onLogout} />;
            case 'dashboard': default: return <AgentDashboardView {...props} onUpdateStatus={setSelectedDelivery} />;
        }
    }

    return (
        <div className="p-4 md:p-6 bg-gray-100 min-h-screen">
            {selectedDelivery && <UpdateStatusModal delivery={selectedDelivery} onClose={() => setSelectedDelivery(null)} onSave={handleSaveStatus} />}
            <header className="mb-6 flex justify-between items-center"><h1 className="text-3xl font-bold">Welcome, {agent.name}</h1><div className="flex gap-2"><TabButton label="Dashboard" isActive={activeView === 'dashboard'} onClick={() => setActiveView('dashboard')} /><TabButton label="History" isActive={activeView === 'history'} onClick={() => setActiveView('history')} /><TabButton label="Profile" isActive={activeView === 'profile'} onClick={() => setActiveView('profile')} /></div></header>
            <main>{renderView()}</main>
        </div>
    );
}

function AgentDashboardView({ deliveries, customers, onUpdateStatus }) {
    const todayDeliveries = deliveries.filter(d => d.status !== 'Delivered' && d.status !== 'Cancelled');
    return (
        <div><h2 className="text-2xl font-bold mb-4">Today's Deliveries</h2>
            {todayDeliveries.length > 0 ? (<div className="space-y-4">{todayDeliveries.map(d => <DeliveryCard key={d.id} delivery={d} customer={customers.find(c=>c.id === d.customerId)} onUpdateStatus={onUpdateStatus} />)}</div>) : (<p>No deliveries for today.</p>)}
        </div>
    );
}

function DeliveryCard({ delivery, customer, onUpdateStatus }) {
    const getMapsUrl = (address) => `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(address)}`;
    return (
        <div className="bg-white p-4 rounded-lg shadow-md border-l-4 border-blue-500">
            <div className="flex justify-between items-start"><p className="font-bold text-lg">{customer?.name}</p><StatusPill status={delivery.status} /></div>
            <p className="text-sm text-gray-600">{customer?.address}</p>
            <div className="mt-4 flex flex-wrap gap-2 items-center"><button onClick={() => onUpdateStatus(delivery)} className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700">Update Status</button><a href={getMapsUrl(customer?.address)} target="_blank" rel="noopener noreferrer" className="bg-gray-700 text-white px-3 py-1 rounded text-sm hover:bg-gray-800">Navigate</a></div>
        </div>
    );
}

function DeliveryHistoryView({ deliveries, customers }) {
    return (
        <div><h2 className="text-2xl font-bold mb-4">Delivery History</h2>
            <div className="space-y-4">{deliveries.map(d => <div key={d.id} className="bg-white p-4 rounded-lg shadow-sm"><p><strong>Customer:</strong> {customers.find(c => c.id === d.customerId)?.name}</p><p><strong>Status:</strong> <StatusPill status={d.status} /></p></div>)}</div>
        </div>
    );
}

function AgentProfileView({ agent, onLogout }) {
    return (
        <div><h2 className="text-2xl font-bold mb-4">My Profile</h2>
            <div className="bg-white p-6 rounded-lg shadow-sm"><p><strong>Name:</strong> {agent.name}</p><p><strong>Email:</strong> {agent.email}</p><button onClick={onLogout} className="mt-6 bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded w-full">Logout</button></div>
        </div>
    );
}

function UpdateStatusModal({ delivery, onClose, onSave }) {
    const [status, setStatus] = useState(delivery.status);
    return (
        <Modal title="Update Delivery Status" onClose={onClose}>
            <div className="space-y-4"><label className="block font-semibold">Status</label><select value={status} onChange={(e) => setStatus(e.target.value)} className="p-2 border rounded w-full"><option value="In Transit">In Transit</option><option value="Delivered">Delivered</option><option value="Cancelled">Cancelled</option></select><button onClick={() => {onSave({...delivery, status}); onClose();}} className="bg-green-500 text-white font-bold py-2 px-4 rounded w-full">Save Update</button></div>
        </Modal>
    );
}

// --- Main App Component ---
export default function App() {
  const [page, setPage] = useState('login');
  const [loggedInUser, setLoggedInUser] = useState(null);
  
  const [customers, setCustomers] = useState(initialCustomers);
  const [agents, setAgents] = useState(initialAgents);
  const [deliveries, setDeliveries] = useState(initialDeliveries);

  const handleLogin = (email, password, userType) => {
    if (userType === 'admin' && email.includes('admin')) {
        setLoggedInUser({ type: 'admin', name: 'Admin' });
        setPage('admin_dashboard');
    } else if (userType === 'agent') {
        const agent = agents.find(a => a.email === email && a.password === password);
        if (agent) {
            setLoggedInUser({ type: 'agent', ...agent });
            setPage('agent_portal');
        } else {
            alert('Invalid agent credentials!');
        }
    } else {
        alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    setLoggedInUser(null);
    setPage('login');
  };

  // --- Data Manipulation Handlers ---
  const handleAddCustomer = (customer) => setCustomers(prev => [...prev, { ...customer, id: Date.now() }]);
  const handleUpdateCustomer = (updated) => setCustomers(customers.map(c => c.id === updated.id ? updated : c));
  const handleDeleteCustomer = (id) => {
      if (window.confirm('Are you sure? This will also delete their deliveries.')) {
          setCustomers(customers.filter(c => c.id !== id));
          setDeliveries(deliveries.filter(d => d.customerId !== id));
      }
  };

  const handleAddAgent = (agent) => setAgents(prev => [...prev, { ...agent, id: Date.now() }]);
  const handleUpdateAgent = (updated) => setAgents(agents.map(a => a.id === updated.id ? updated : a));
  const handleDeleteAgent = (id) => {
      if (window.confirm('Are you sure? This will unassign their deliveries.')) {
          setAgents(agents.filter(a => a.id !== id));
          setDeliveries(deliveries.map(d => d.agentId === id ? { ...d, agentId: null } : d));
      }
  };

  const handleCreateDelivery = (delivery) => setDeliveries(prev => [...prev, { ...delivery, id: Date.now(), status: 'Pending' }]);
  const handleUpdateDelivery = (updated) => setDeliveries(deliveries.map(d => d.id === updated.id ? updated : d));
  const handleDeleteDelivery = (id) => {
      if (window.confirm('Are you sure?')) {
          setDeliveries(deliveries.filter(d => d.id !== id));
      }
  };

  switch (page) {
    case 'admin_dashboard':
      return <AdminDashboard onLogout={handleLogout} customers={customers} agents={agents} deliveries={deliveries} onAddCustomer={handleAddCustomer} onAddAgent={handleAddAgent} onCreateDelivery={handleCreateDelivery} onUpdateCustomer={handleUpdateCustomer} onDeleteCustomer={handleDeleteCustomer} onUpdateAgent={handleUpdateAgent} onDeleteAgent={handleDeleteAgent} onUpdateDelivery={handleUpdateDelivery} onDeleteDelivery={handleDeleteDelivery} />;
    case 'agent_portal':
      return <AgentPortal agent={loggedInUser} onLogout={handleLogout} allDeliveries={deliveries} allCustomers={customers} onUpdateDelivery={handleUpdateDelivery} />;
    case 'login':
    default:
      return <LoginPage onLogin={handleLogin} />;
  }
}
